insert into documents(url,title,publication_date,modified_date,author,publisher,affected_donors,affected_donees,document_scope,cause_area,notes) values
  ('https://www.openphilanthropy.org/blog/suggestions-individual-donors-open-philanthropy-project-staff-2016','Suggestions for Individual Donors from Open Philanthropy Project Staff - 2016','2016-12-14',NULL,'Holden Karnofsky','Open Philanthropy Project','Jaime Yassif,Chloe Cockburn,Lewis Bollard,Daniel Dewey,Nick Beckstead','Blue Ribbon Study Panel on Biodefense,Alliance for Safety and Justice,Cosecha,Animal Charity Evaluators,Compassion in World Farming USA,Machine Intelligence Research Institute,Future of Humanity Institute,80000 Hours,Ploughshares Fund','Donation suggestion list','Animal welfare,AI risk,Biosecurity and pandemic preparedness,Effective altruism,Migration policy','Open Philanthropy Project staff describe suggestions for best donation opportunities for individual donors in their specific areas'),
  ('https://blog.givewell.org/2015/12/23/suggestions-for-individual-donors-from-open-philanthropy-project-staff/','Suggestions for individual donors from Open Philanthropy Project staff','2015-12-23','2016-01-03','Holden Karnofsky','Open Philanthropy Project','Chloe Cockburn,Lewis Bollard,Alexander Berger,Nick Beckstead,Howie Lempel','Alliance for Safety and Justice,Bronx Freedom Fund,The Humane League,The Humane Society of the United States,Center for Global Development,Center for Popular Democracy,Ploughshares Fund','Donation suggestion list','Criminal justice reform,animal welfare,Global health','Open Philanthropy Project stff describe suggestions for best donation opportunities for individual donors in their specific areas. The post is originally published to the GiveWell blog');
insert into documents(url,title,publication_date,modified_date,author,publisher,affected_donors,affected_donees,document_scope,cause_area,notes) values
  ('https://www.openphilanthropy.org/blog/staff-members-personal-donations-giving-season-2017','Staff Members’ Personal Donations for Giving Season 2017','2017-12-18',NULL,'Holden Jarnofsky','Open Philanthropy Project','Holden Karnofsky,Alexander Berger,Nick Beckstead,Helen Toner,Claire Zabel,Lewis Bollard,Ajeya Cotra,Morgan Davis,Michael Levine','GiveWell top charities,GiveWell,GiveDirectly,EA Giving Group,Effective Altruism Funds,Sentience Institute,Encompass,The Humane League,The Good Food Institute,Mercy For Animals,Compassion in World Farming USA,Animal Equality,Donor lottery,Against Malaria Foundation,GiveDirectly','Periodic donation list documentation',NULL,'Open Philanthropy Project staff members describe where they are donating this year, and the considerations that went into the donation decision. By policy, amounts are not disclosed. This is the first standalone blog post of this sort by the Open Philanthropy Project; in previous years, the corresponding donations were documented in the GiveWell staff members donation post');
